sp_widget.client_script:
function($scope, spUtil, u_cutassp) {	
	if (u_cutassp.utilIsValueValidAndTrue($scope.data.isDebugOn)) {
		console.log($scope.data.infotag+(new Date()).getTime());		
	}
	//
	var c = this;
	c.plusMinusSymbol='fa fa-plus-square m-r-sm';	
	$rootScope.isKbCatShow = false;
	c.myLog = function() {
		if (u_cutassp.utilIsValueValidAndTrue($scope.data.isDebugOn)) {
			console.log($scope.data.infotag.replace(':',' HTMl view:')+(new Date()).getTime());
		}
		return '';
	};
	//	
	//** Show/Hide 
	function get() {
		spUtil.update($scope);
	}

	$scope.myShowHide = function(e) {
		$rootScope.isKbCatShow = !$rootScope.isKbCatShow;
		c.plusMinusSymbol = $rootScope.isKbCatShow ? 'fa fa-minus-square m-r-sm' : 'fa fa-plus-square m-r-sm';

		var myEl=e.currentTarget;
		var myRowDiv=$(myEl).closest('div.sp-row-content');
		var myRow=myRowDiv.parent();
		var myWidgetRow1=myRow.next();
		var myWidgetRow2=myWidgetRow1.next();
		myWidgetRow1.toggle();
		myWidgetRow2.toggle();		
	};

}

