sp_widget.client_script:
function($scope,$rootScope, $element, spUtil, $window,u_cutassp) {	
	if (u_cutassp.utilIsValueValidAndTrue($scope.data.isDebugOn)) {
		console.log($scope.data.infotag+(new Date()).getTime());		
	}
	//
	var c = this;		
	c.myLog = function() {
		if (u_cutassp.utilIsValueValidAndTrue($scope.data.isDebugOn)) {
			console.log($scope.data.infotag.replace(':',' HTMl view:')+(new Date()).getTime());
		}
		return '';
	};
	c.isViewTickets = false;
	//	
	debugger;

	$scope.myShowHide = function(e) {
		var myEl=e.currentTarget;
		var myI=$(myEl).children('i').first();
		myI.toggleClass('fa-plus-square');
		myI.toggleClass('fa-minus-square');
		c.isViewTickets = !c.isViewTickets;
	};

}

sp_widget.script:
(function($sp, input, data, options, gs){
	var myU = new u_utassp();
	// *** Start: Debug only code *** //
	data.isDebugOn = false;
	if (myU.utilIsValueValidAndTrue(gs.getProperty('x_287765_utassp.utassp_isDebugOn')))
	{
		data.widgettitle =  'utassp01_view_tickets_widget > '+$sp.getDisplayValue("title");
		data.widgetpage = myU.utilIsValueValid($sp.getParameter('id')) ? $sp.getParameter('id') : 'utassp01_index';
		data.isDebugOn = true;
		data.infotag = data.widgetpage+' > '+data.widgettitle+': ';
		gs.info(data.infotag +(new Date()).getTime());		
	}
	// *** End: Debug only code *** //
	//
	///* populate the 'data' object */  /* e.g., data.table = $sp.getValue('table'); */
	data.isLoggedIn = gs.isLoggedIn();
	//
})($sp, input, data, options, gs);
