sp_widget.client_script:
function($scope, spUtil, u_cutassp) {	
	if (u_cutassp.utilIsValueValidAndTrue($scope.data.uSettings.isDebugOn)) {
		console.log((new Date()).getTime()+'.Client - '+$scope.data.infotag);
	}
	//
	/* widget controller */
	var c = this;

	c.myLabel = function (_p) { return u_cutassp.fmtLabel(_p); };
	c.myLog = function() {
		if (u_cutassp.utilIsValueValidAndTrue($scope.data.uSettings.isDebugOn)) {
			console.log((new Date()).getTime()+'.HTML View - '+$scope.data.infotag);
		}
		return '';
	};
	//
	//** Show/Hide 
	function get() {
		spUtil.update($scope);
	}

	$scope.myShowHide = function(e) {
		$rootScope.isKbCatShow = !$rootScope.isKbCatShow;
		c.plusMinusSymbol = $rootScope.isKbCatShow ? 'fa fa-minus-square m-r-sm' : 'fa fa-plus-square m-r-sm';

		var myEl=e.currentTarget;
		var myRowDiv=$(myEl).closest('div.sp-row-content');
		var myRow=myRowDiv.parent();
		var myWidgetRow1=myRow.next();
		var myWidgetRow2=myWidgetRow1.next();
		myWidgetRow1.toggle();
		myWidgetRow2.toggle();		
	};

}

