sp_widget.client_script:
function($scope, $rootScope, spUtil) {
	c = this;
	c.scope = $scope;
	$scope.attachments=[];
	//
	$scope.myAddAttachmentClick= function(e) {
		e.currentTarget = "#add_attachment_button";
		$rootScope.$broadcast('myAddAttachmentEvent', e);
		$('#divListMsgId').show();
	};
	//
	$rootScope.$on('myAttachmentListEvent', function(e,aList) {
		alert('back');
		debugger;
		c.scope.attachments=aList;
	});	
}

sp_widget.css:
div.panel-heading {
  margin-bottom:10px;
}

