sp_widget.client_script:
function($scope,$rootScope,$timeout,$location,$window,u_cutassp) {
	if (u_cutassp.utilIsValueValidAndTrue($scope.data.isDebugOn)) {
		console.log($scope.data.infotag+(new Date()).getTime());		
	}
	//
	/* widget controller */
	var c = this;
	c.data.second = true;
	var paramList = $location.search();
	var isHomePage = false;
	if (u_cutassp.utilIsValueValid(paramList.id)) {
		if (paramList.id.toString().indexOf('index')>-1) {
			isHomePage = true;
		}		
	}
	else {
		isHomePage = true;
	}
	if (isHomePage)
	{
		//We are at the home page, so reset root breadcrumb stack
		$rootScope.bc = [];
	}
	//
	c.viewContentLoadedEventCtr = -1;
	$scope.$watch('$viewContentLoaded', function(newValue, oldValue, scope)
    {
		console.log('HomePage.$viewContentLoaded... '+scope.c.viewContentLoadedEventCtr);
		window.dataLayer = window.dataLayer || [];
		function gtag(){dataLayer.push(arguments);}
		if (scope.c.viewContentLoadedEventCtr<0) {
			console.log('HomePage.$viewContentLoaded -- GA');
			gtag('js', new Date());
			gtag('config', 'UA-135382660-1');		
		}
		scope.c.viewContentLoadedEventCtr++;
    });				

	c.server.update().then(function(){
		if (u_cutassp.utilIsValueValidAndTrue($scope.data.isDebugOn)) {
			console.log($scope.data.infotag+(new Date()).getTime()); 
		}
	});					
}

sp_widget.script:
(function($sp, input, data, options, gs) {
	var myU = new u_utassp();	
	// *** Start: Debug only code ***
	data.isDebugOn = false;
	if (myU.utilIsValueValidAndTrue(gs.getProperty('x_287765_utassp.utassp_isDebugOn')))
	{
		data.isDebugOn = true;	
		data.widgettitle =  'utassp01_bread_data_std_widget > '+$sp.getDisplayValue("title");
		data.widgetpage = myU.utilIsValueValid($sp.getParameter('id')) ? $sp.getParameter('id') : 'utassp01_index';
		data.isSecondTime = false;
		if (myU.utilIsValueValid(input)) {
			data.isSecondTime = true;
			data.infotag = data.widgetpage+' > '+data.widgettitle+' 2nd Time: ';	
		} else {
			data.infotag = data.widgetpage+' > '+data.widgettitle+': ';			
		}		
		gs.info(data.infotag +(new Date()).getTime());		
	}
	// *** End: Debug only code ***
})($sp, input, data, options, gs);
