sp_widget.client_script:
function($scope,$rootScope,$timeout, u_cutassp) {
	if (u_cutassp.utilIsValueValidAndTrue($scope.data.uSettings.isDebugOn)) {
		console.log((new Date()).getTime()+'.Client - '+$scope.data.infotag);
	}
	//
	/* widget controller */
	var c = this;

	c.myLabel = function (_p) { return u_cutassp.fmtLabel(_p); };
	c.myLog = function() {
		if (u_cutassp.utilIsValueValidAndTrue($scope.data.uSettings.isDebugOn)) {
			console.log((new Date()).getTime()+'.HTML View - '+$scope.data.infotag);
		}
		return '';
	};
	//
	c.data.accessMsg = "Students, faculty & staff: Login to access further services.";
	c.data.isLoggedIn = (typeof $scope.user.first_name == typeof myUndefined) ? false : true;
	c.data.welcome = c.data.isLoggedIn ? $scope.user.first_name : 'Welcome to UTA\'s Service Portal' ;
}

sp_widget.script:
(function($sp, input, data, options, gs) {
	var myU = new u_utassp();
	data.uSettings=myU.utilGetInstanceSettings($sp);
	// *** Start: Debug only code *** //
	data.infotag = '';
	if (myU.utilIsValueValidAndTrue(data.uSettings.isDebugOn)) {
		data.infotag = myU.utilLog($sp);
	}
	// *** End: Debug only code *** //
	//
	data.isLoggedIn = gs.isLoggedIn();
	data.typeAheadSearch = $sp.getWidget('utassp02_typeahead_search_widget', options.typeahead_search);
})($sp, input, data, options, gs);
