sp_widget.client_script:
function($scope, spUtil, u_cutassp) {	
	if (u_cutassp.utilIsValueValidAndTrue($scope.data.uSettings.isDebugOn)) {
		console.log((new Date()).getTime()+'.Client - '+$scope.data.infotag);
	}
	//
	/* widget controller */
	var c = this;

	c.myLabel = function (_p) { return u_cutassp.fmtLabel(_p); };
	c.myLog = function() {
		if (u_cutassp.utilIsValueValidAndTrue($scope.data.uSettings.isDebugOn)) {
			console.log((new Date()).getTime()+'.HTML View - '+$scope.data.infotag);
		}
		return '';
	};
	//
	c.isOpenTicketsSelected=true;
	c.isClosedTicketsSelected=!c.isOpenTicketsSelected;


	$scope.myMenuClick = function(e)
	{
		var myEl=$(e.currentTarget);	
		var myLiSiblings = myEl.parent().siblings();
		var myAlinkActive = myLiSiblings.children('a.active');
		if (myAlinkActive.length>0) {
			myAlinkActive.toggleClass('active');
		}
		myEl.toggleClass('active');
		c.isClosedTicketsSelected=c.isOpenTicketsSelected;		
		c.isOpenTicketsSelected=!c.isOpenTicketsSelected;
	};
}

