sp_widget.client_script:
function($scope,$rootScope,$timeout, u_cutassp) {
	if (u_cutassp.utilIsValueValidAndTrue($scope.data.isDebugOn))
	{
		console.log($scope.data.infotag+(new Date()).getTime());		
	}
	//
	/* widget controller */
	var c = this;
	c.myLog = function() {
		if (u_cutassp.utilIsValueValidAndTrue($scope.data.isDebugOn)) {
			console.log($scope.data.infotag.replace(':',' HTMl view:')+(new Date()).getTime());
		}
		return '';
	};
	//
	c.data.isLoggedIn = (typeof $scope.user.first_name == typeof myUndefined) ? false : true;
	c.data.welcome = c.data.isLoggedIn ? $scope.user.first_name : 'Welcome, Guest!' ;
}

