sp_widget.client_script:
function ($scope, spUtil, snRecordWatcher, $rootScope, u_cutassp) { 
	if (u_cutassp.utilIsValueValidAndTrue($scope.data.uSettings.isDebugOn)) {
		console.log((new Date()).getTime()+'.Client - '+$scope.data.infotag);		
	}
	//
	/* widget controller */
	var c = this;

	c.myLabel = function (_p) { return u_cutassp.fmtLabel(_p); };
	c.myLog = function() {
		if (u_cutassp.utilIsValueValidAndTrue($scope.data.uSettings.isDebugOn)) {
			console.log((new Date()).getTime()+'.HTML View - '+$scope.data.infotag);
		}
		return '';
	};
	//
	$scope.standalone = $scope.options.standalone == 'true' || $scope.options.standalone == true;
	var q = "cmdb_ci.sys_class_name=cmdb_ci_service";
	if ($scope.data.outageIDs != "")
		q += "^NQ" + $scope.data.outageIDs;

	snRecordWatcher.initList("cmdb_ci_outage", q);

	function get() {
		$rootScope.$broadcast("sp.outage.updated");
		spUtil.update($scope);
	}

	$scope.$on('record.updated', get);
}

